export const THEME_WORDBANKS: ThemeWordbank[] = [
  COMPLETE_ENGLISH_WORD_BOOK
];

// 根据ID获取主题词库
export function getThemeWordbankById(id: string): ThemeWordbank | null {
  return THEME_WORDBANKS.find(bank => bank.id === id) || null;
}

// 获取所有主题词库
export function getAllThemeWordbanks(): ThemeWordbank[] {
  return THEME_WORDBANKS;
}